fun main() {
    val KFC = FastFood("Kawaii Fried Chicken")
    Scanner(System.`in`).use {
        println("welcum to ${KFC.name}, can i take your oworder? ^_^")
        while (it.hasNextInt()) {
            KFC.sellOrder(it.nextInt())
            println("you want more of our thighs?")
        }
    }
}
class FastFood(val name: String) {
    var stock = 0

    fun orderStock(amount: Int) {
        println("ordering $amount pieces of chicken thighs")
        stock += amount
    }

    fun sellOrder(order: Int) {
        if (stock - order <= 0)
            orderStock((order * 1.5).roundToInt())
        println("decreasing the stock by $order")
        stock -= order
    }
}
